accountingMethods.config:
aaa accounting commands 0 default start-stop group {{tacacs.serverGroup}}
aaa accounting commands 1 default start-stop group {{tacacs.serverGroup}}
aaa accounting commands 15 default start-stop group {{tacacs.serverGroup}}

criticalAuthSettings.config:
service-template CRITICAL_DATA_ACCESS
 vlan {{dataVlan}}
 access-group ACL-OPEN
service-template CRITICAL_VOICE_ACCESS
 voice vlan {{voiceVlan}}
 access-group ACL-OPEN
authentication critical recovery delay {{recoveryDelay}}

acls.config:
ip access-list extended ACL-AUTH-SERVER
 permit udp any eq bootpc any eq bootps
 permit udp any any eq domain
 permit ip any host {{radius.servers[0].ip}}
 permit ip any host {{radius.servers[1].ip}}
 deny ip any any

radsec.serverConfig:
radius server {{radsec.serverName}}
 address ipv4 {{radsec.serverIP}} auth-port 2083 acct-port 2083
 tls connectiontimeout 5
 tls trustpoint client {{radsec.clientTrustpoint}}
 tls trustpoint server {{radsec.serverTrustpoint}}
 key {{radsec.serverKey}}

